import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from "react-router-dom";

import "./Login.css";
import logo from "../../Imagens/Log.png";
import ou from "../../Imagens/ou.png";
import "../../Fonts.css";
import axios from 'axios';
import { UserContext } from '../../UserContext';

function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [authenticated, setAuthenticated] = useState(false);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const navigate = useNavigate();
  const { email, setEmail } = useContext(UserContext);




  useEffect(() => {
    const handleTouchMove = (event) => {
      if (event.touches.length > 1) {
        event.preventDefault(); // Impede o comportamento padrão do gesto de zoom
      }
    };

    document.addEventListener("touchmove", handleTouchMove, { passive: false });

    return () => {
      document.removeEventListener("touchmove", handleTouchMove);
    };
  }, []);



  function handleLogin() {
    // Lógica de autenticação do usuário aqui

    const requestBody = {
      email: username,
      password: password
    };

    const url = 'http://192.168.0.9:8080/professional/authentication';

    axios.post(url, requestBody)
      .then(response => {
        console.log('Status code:', response.status);
        console.log('Response body:', response.data);
        
        if (response.status === 200) {
          // Autenticação bem-sucedida
          setAuthenticated(true);
          // Navegue para a tela Inicio
          setError("");
          setSuccessMessage("Login efetuado com sucesso!"); // Define a mensagem de sucesso
          setTimeout(() => {
          setSuccessMessage(""); // Limpa a mensagem de sucesso após 2 segundos
          setEmail(username);
          navigate("/Inicio");// Navega para a tela desejada
        }, 2000);
        } else {
          setError("Credenciais inválidas");
        }
      })
      .catch(error => {
        console.error('Error:', error);
      });
  }

  function handleCriarConta() {
    if (authenticated) {
      console.log("Você já está autenticado");
      return;
    }
    else{
      navigate("/CriarConta");
    }
      
    }


  function handleForgotPassword() {
    // Lógica para recuperar a senha
  }

  return (
    <div className="login-container">
      <div className="login-form">
        <h1 className="titlog">LOGIN</h1>
        <img className="ImagemLogin" src={logo} alt="Imagem de Login" />
        <div className="campos1-form">
          <input
            type="text"
            id="username"
            value={username}
            placeholder="Usuário:"
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className="campos2-form">
          <input
            type="password"
            id="password"
            value={password}
            placeholder="Senha:"
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        
        {successMessage && <p className="success-message">{successMessage}</p>}
        <button className="login-button" onClick={handleLogin}>
          ENTRAR
        </button>
        <button className="link-button" onClick={handleForgotPassword}>
          Esqueci minha senha
        </button>
        {error && <p className="error-message">{error}</p>}
        <img src={ou} alt="ou" style={{ width: '290px', marginBottom: '2%' }} />

        
        <button className="Criar-button" onClick={handleCriarConta}>
          Criar uma nova conta
        </button>
        
      </div>
    </div>
  );
}

export default Login;
