import React, { useState, useMemo, useEffect, useContext } from "react";
import { UserContext } from "./../../UserContext";
import axios from "axios";
import './Pacientes.css';
import Menu from '../../Menu';
import Header from '../../Header';
import Widget from './CadPaciente';
import { useTable, useFilters } from 'react-table';

function ColumnFilter({ column }) {
  const { filterValue, setFilter } = column;

  return (
    <input
      type="text"
      value={filterValue || ''}
      onChange={(e) => setFilter(e.target.value)}
      placeholder="Filtrar"
    />
  );
}

const Pacientes = () => {
  const [valueInput, setValueInput] = useState('');
  const [filteredData, setFilteredData] = useState([]);
  const [pacientes, setPacientes] = useState([]);
  const [nomes, setNomes] = useState([]);
  const { crp } = useContext(UserContext);
  const [showWidget, setShowWidget] = useState(false);
  const handleValueChange = (e) => {
    const value = e.target.value || '';
    setValueInput(value);

    const filtered = pacientes.filter((item) => item.name.toLowerCase().includes(value.toLowerCase()));
    setFilteredData(filtered);
  };

  const columns = useMemo(
    () => [
      {
        Header: 'Nome',
        accessor: 'name',
        Filter: ColumnFilter,
      },
      {
        Header: 'Email',
        accessor: 'emailAddress',
      },
      {
        Header: 'Telefone',
        accessor: 'phoneNumber',
      },
      {
        Header: 'Ação',
        accessor: 'acao',
      },
    ],
    []
  );

  function toggleWidget() {    
    setShowWidget((prevState) => !prevState);
  }

  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = useTable(
    {
      columns,
      data: filteredData,
    },
    useFilters
  );

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://192.168.0.9:8080/professional/patients/${crp}`);
        const pacientes = response.data.object;
        setPacientes(pacientes);
        const names = pacientes.map((item) => item.name);
        names.sort();
        if (Array.isArray(names)) {
          setNomes(names);
        } else {
          console.error("Os dados obtidos não são um array:", names);
        }
      } catch (error) {
        console.error("Erro ao obter os nomes da base de dados:", error);
      }
    };

    fetchData();
  }, [crp]);

  useEffect(() => {
    setFilteredData(pacientes);
  }, [pacientes]);

  return (
    <div className="container">
      <div className="item item1-1" style={{ width: '93%' }}>
        <Menu />
      </div>
      <div className="item item1-2">
        <Header />
      </div>
      
      <div className="item itemP2-1">      
        <h1 className="tituloprinc">Gerenciamento de Pacientes</h1>
        <div className="input-container" style={{marginTop:"3%"}}>          
          <button className="circle-buttonP" onClick={toggleWidget}>Cadastrar Paciente</button>    
          {showWidget && (
            <div className="widget-container2">
              <Widget/>                
            </div>              
          )}      
          <input className="Pesquisa" type="text" value={valueInput} onChange={handleValueChange} placeholder={'Busca pelo nome'} />
        </div>
        
        <table style={{ width: '100%' }} className="tableP" {...getTableProps()}>
          <thead className="theadP">
            {headerGroups.map((headerGroup) => (
              <tr {...headerGroup.getHeaderGroupProps()} key={headerGroup.id}>
                {headerGroup.headers.map((column) => (
                  <th className="thP" {...column.getHeaderProps()} key={column.id}>
                    {column.render('Header')}
                    <div>{column.canFilter ? column.render('Filter') : null}</div> {/* Adicione essa linha para renderizar o filtro */}
                  </th>
                ))}
              </tr>
            ))}
          </thead>
          <tbody {...getTableBodyProps()}>
            {rows.map((row) => {
              prepareRow(row);
              return (
                <tr {...row.getRowProps()} key={row.id}>
                  {row.cells.map((cell) => (
                    <td className="tdP" {...cell.getCellProps()} key={cell.column.id}>
                      {cell.render('Cell')}
                    </td>
                  ))}
                </tr>
              );
            })}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Pacientes;
