import { useState, useContext } from "react";
import { useNavigate } from "react-router-dom";
import { UserContext } from "./../../UserContext";
import exit from "./../../Imagens/exit.png";
import axios from 'axios';
import "./CadPaciente.css";

function CadPaciente() {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const [isConfigOpen, setIsConfigOpen] = useState(true);
  const [cpf, setCpf] = useState("");
  const [name, setName] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [dateOfBirth, setDateOfBirth] = useState("");
  const [emailAddress, setEmailAddress] = useState("");
  const [city, setCity] = useState("");
  const [state, setState] = useState("");
  const [country, setCountry] = useState("");
  const [street, setStreet] = useState("");
  const [postalCode, setPostalCode] = useState("");
  const [responsable, setReponsable] = useState("");
  const { crp } = useContext(UserContext);
  
  function CadastraPac() {
    setIsLoading(true);
  
    const requestBody = {
      cpf: cpf,
      name: name,
      phoneNumber: phoneNumber,
      dateOfBirth: dateOfBirth,
      emailAddress: emailAddress,
      city: city,
      state: state,
      country: country,
      street: street,
      postalCode: postalCode,
      responsible: responsable
    };
  
    axios
      .post("http://192.168.0.9:8080/patient/${crp}", requestBody)
      .then((response) => {
        setIsLoading(false);
        navigate("/Pacientes");
      })
      .catch((error) => {
        setIsLoading(false);
        console.error("Error creating patient:", error);
      });
    }  

  function closeConfig() {
    setIsConfigOpen(false);
  }
  if (isConfigOpen===false) {
    return null;
  }
  async function fetchAddress() {
    const cep = postalCode.replace(/\D/g, ''); // Remove caracteres não numéricos do CEP
  
    try {
      const response = await axios.get(`https://viacep.com.br/ws/${cep}/json/`);
      const addressData = response.data;
  
      setState(addressData.uf); // Atualiza o campo "Estado" com a UF retornada pela API
      setCountry('Brasil'); // Define o país como "Brasil"
      setCity(addressData.localidade); // Atualiza o campo "Cidade" com a cidade retornada pela API
  
    } catch (error) {
      console.error('Error:', error);
      setCity("");
      setState("");
      setCountry("");
    }
  }






  return (
<div className="Cadpac" style={{ position: "fixed", top: "50%", left: "9%", transform: "translate(-50%, -50%)", zIndex: 9999, width: "300px", backgroundColor: "White", border: "0.1px solid grey", borderRadius: "10px", padding: "0.3%" }}>
      <div className="titleContainer">
      <h2>Cadastrar novo paciente</h2>
      <button className="bntfechar"  onClick={closeConfig}><img src={exit} alt="Imagem de Login" /></button>
      </div>
      
      <div className="campo-form">
        <input className="input-c"
          type="text"
          value={cpf}
          placeholder="CPF:"
          onChange={(e) => setCpf(e.target.value)}
        />
      </div>
      <div className="campo-form">
        <input className="input-c"
          type="text"
          value={name}
          placeholder="Nome:"
          onChange={(e) => setName(e.target.value)}
        />
      </div>
      <div className="campo-form">
        <input className="input-c"
          type="text"
          value={phoneNumber}
          placeholder="Telefone:"
          onChange={(e) => setPhoneNumber(e.target.value)}
        />
      </div>
      <div className="campo-form">
        <input className="input-c"
          type="text"
          value={dateOfBirth}
          placeholder="Data de Nascimento (dd/mm/aaaa):"
          onChange={(e) => setDateOfBirth(e.target.value)}
        />
      </div>
      <div className="campo-form">
        <input className="input-c"
          type="email"
          value={emailAddress}
          placeholder="Endereço de Email:"
          onChange={(e) => setEmailAddress(e.target.value)}
        />
      </div>
      <div className="campo-form">
        <input className="input-c"
          type="text"
          value={postalCode}
          placeholder="CEP:"
          onChange={(e) => setPostalCode(e.target.value)}
          onBlur={fetchAddress}
      />

      </div>
 
      <div className="campo-form">
        <input className="input-c"
          type="text"
          value={street}
          placeholder="Rua, número e complemento (se houver):"
          onChange={(e) => setStreet(e.target.value)}
        />
      </div>

      <div className="campo-form">
        <input className="input-c"
          type="text"
          value={city}
          placeholder="Cidade:"
          onChange={(e) => setCity(e.target.value)}          
        />
      </div>
      <div className="campo-form">
        <input className="input-c"
          type="text"
          value={state}
          placeholder="Estado:"
          onChange={(e) => setState(e.target.value)}          
        />
      </div>
      <div className="campo-form">
        <input className="input-c"
          type="text"
          value={country}
          placeholder="País:"
          onChange={(e) => setCountry(e.target.value)}          
        />
      </div>
      <div className="campo-form">
        <input className="input-c"
          type="text"
          value={responsable}
          placeholder="Responsável:"
          onChange={(e) => setReponsable(e.target.value)}          
        />
      </div>
      
    
      <button className="criar-conta-button" onClick={CadastraPac}>
        Criar Conta
      </button>
      {isLoading && (
        <>
          <div className="loading-bar2">
            <div className="loading-bar2-fill" />
          </div>
          <p style={{ fontSize: "12px" }}>Fazendo Logoff...</p>
        </>
      )}

    </div>
  );
}

export default CadPaciente;
