import React from 'react';
import "./Inicio.css"; 
import Menu from "../../Menu";
import Header from '../../Header';
import Dia from '../../Dia';
import moment from 'moment';

function getDaysInMonth(month, year) {
  const date = moment(`${year}-${month}-01`);
  const days = [];

  while (date.month() === month - 1) {
    days.push(date.clone());
    date.add(1, 'day');
  }

  return days;
}

function chunkArray(arr, chunkSize) {
  const chunks = [];
  for (let i = 0; i < arr.length; i += chunkSize) {
    chunks.push(arr.slice(i, i + chunkSize));
  }
  return chunks;
}

const Inicio = () => {
  const days = getDaysInMonth(moment().month() + 1, moment().year());
  const weeks = chunkArray(days, 7);

  return (
    <div className="container">       
      <div className="item item1-1"><Menu /></div>
      <div className="item item1-2"><Header/></div>
      <div className="item item2">
        <h1>{moment().format('MMMM YYYY')}</h1>
        <table style={{ width: "100%", gridColumn: "2 / 3" }}>
          <thead>
            <tr>
              <th></th>
              <th>Seg</th>
              <th>Ter</th>
              <th>Qua</th>
              <th>Qui</th>
              <th>Sex</th>
              <th>SÃ¡b</th>
              <th>Dom</th>
            </tr>
          </thead>
          <tbody>
            {weeks.map((week, i) => (
              <tr key={i}>
                <td>{week[0].format('W')}</td>
                {week.map((day) => (
                  <td key={day.format('YYYY-MM-DD')}>
                    <h2>Title 1</h2>
                    <h2>Title 2</h2>
                    <button>Button 1</button>
                    <button>Button 2</button>
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Inicio;
