import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import "./Widget.css";
import { UserContext } from '../../../UserContext';

const menuHeight = getComputedStyle(document.documentElement).getPropertyValue("--menu-height");

const buttons = ["Dados do Paciente", "Dados da Consulta", "Arquivos", "Finalizar"];

function Widget() {
  const [activeBlock, setActiveBlock] = useState(0);
  const [isWidgetOpen, setIsWidgetOpen] = useState(true);
  const { crp } = useContext(UserContext);
  const [formValues, setFormValues] = useState({
    nome: "",
    cpf: "",
    email: "",
    local: "",
    recorrencia: "",
    dataInicio: "",
    dataFim: "",
    horario: "",
    preco: "",
  });
  const [nomes, setNomes] = useState([]);

  const toggleMenuBlock = (index) => {
    if (index === activeBlock || canSwitchBlock()) {
      setActiveBlock(index);
    }
  };

  const closeWidget = (event) => {
    event.stopPropagation();
    setIsWidgetOpen(false);
  };

  const canSwitchBlock = () => {
    switch (activeBlock) {
      case 0:
        return formValues.nome !== "" && formValues.cpf !== "" && formValues.email !== "";
      case 1:
        return (
          formValues.local !== "" &&
          formValues.recorrencia !== "" &&
          formValues.dataInicio !== "" &&
          formValues.dataFim !== "" &&
          formValues.horario !== "" &&
          formValues.preco !== ""
        );
      case 2:
        return true;
      default:
        return false;
    }
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormValues((prevValues) => ({
      ...prevValues,
      [name]: value,
    }));
  };

  const handleRecorrenciaChange = (event) => {
    const { value } = event.target;
    setFormValues((prevValues) => ({
      ...prevValues,
      recorrencia: value,
    }));
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://192.168.0.9:8080/professional/patients/${crp}`);
        const data = response.data;
        const names = data.object.map((item) => item.name);
        if (Array.isArray(names)) {
          setNomes(names);
        } else {
          console.error("Os dados obtidos não são um array:", names);
        }
      } catch (error) {
        console.error("Erro ao obter os nomes da base de dados:", error);
      }
    };

    fetchData(); // Chama a função fetchData para buscar os nomes
  }, [crp]);

  const handleNomeChange = (event) => {
    const { value } = event.target;
    const selectedPatient = data.object.find(item => item.name === value);
    if (selectedPatient) {
      const { cpf, email } = selectedPatient;
      setFormValues((prevValues) => ({
        ...prevValues,
        cpf: cpf,
        email: email,
        nome: value,
      }));
    }
  };

  if (!isWidgetOpen) {
    return null;
  }

  return (
    <article className={`card ${activeBlock === 0 ? "open" : ""}`}>
      <div className="buttons">
        {buttons.map((button, index) => (
          <button
            className={index === activeBlock ? "active" : ""}
            onClick={() => toggleMenuBlock(index)}
            key={index}
          >
            {button}
          </button>
        ))}
        <button className="close-button" onClick={closeWidget}>
          FECHAR
        </button>
      </div>

      <div className="wrapper">
        <div className="content" style={{ transform: `translate(0, calc(0px - ${menuHeight} * ${activeBlock}))` }}>
          <div className="block">
            <h2>Dados do Paciente</h2>
            <select
              className="campo-form2"
              value={formValues.nome}
              onChange={handleNomeChange}
              name="nome"
              required
            >
              <option value="" disabled>
                Selecione o nome:
              </option>
              {nomes.map((nome, index) => (
                <option key={index} value={nome}>
                  {nome}
                </option>
              ))}
            </select>
            <input
              className="campo-form2"
              type="text"
              maxLength="11"
              placeholder="CPF:"
              pattern="\d{3}\.\d{3}\.\d{3}-\d{2}"
              title="Digite um CPF válido no formato XXXXXXXXXXX"
              name="cpf"
              value={formValues.cpf}
              onChange={handleInputChange}
              onKeyDown={(e) => {
                if (
                  !(
                    (e.key >= "0" && e.key <= "9") ||
                    e.key === "Backspace" ||
                    e.key === "Delete" ||
                    e.key === "Tab" ||
                    e.key === "ArrowLeft" ||
                    e.key === "ArrowRight" ||
                    e.key === "-"
                  )
                ) {
                  e.preventDefault();
                }
              }}
              required
            />

            <input
              className="campo-form2"
              type="text"
              placeholder="E-mail:"
              name="email"
              value={formValues.email}
              onChange={handleInputChange}
              required
            />
          </div>
          <div className="block">
            <h2>Dados da Consulta</h2>
            <input
              className="campo-form2"
              type="text"
              placeholder="Local:"
              name="local"
              value={formValues.local}
              onChange={handleInputChange}
              required
            />

            <select
              className="campo-form2"
              value={formValues.recorrencia}
              onChange={handleRecorrenciaChange}
              required
            >
              <option value="" disabled>
                Recorrência:
              </option>
              <option value="virtual">Virtual</option>
              <option value="presencial">Presencial</option>
            </select>


            <div className="input-container">
              <input
                className="campo-form2"
                type="text"
                style={{ width: "0%" }}
                pattern="\d{2}\/\d{2}\/\d{4}"
                onKeyDown={(e) => {
                  if (
                    !(
                      (e.key >= "0" && e.key <= "9") ||
                      e.key === "Backspace" ||
                      e.key === "Delete" ||
                      e.key === "Tab" ||
                      e.key === "ArrowLeft" ||
                      e.key === "ArrowRight" ||
                      e.key === "/"
                    )
                  ) {
                    e.preventDefault();
                  }
                }}
                required
                placeholder="Data início:"
                data-tip="Formato DD/MM/AAAA"
                name="dataInicio"
                value={formValues.dataInicio}
                onChange={handleInputChange}
              />
              <input
                className="campo-form2"
                type="text"
                style={{ width: "50%" }}
                pattern="\d{2}\/\d{2}\/\d{4}"
                onKeyDown={(e) => {
                  if (
                    !(
                      (e.key >= "0" && e.key <= "9") ||
                      e.key === "Backspace" ||
                      e.key === "Delete" ||
                      e.key === "Tab" ||
                      e.key === "ArrowLeft" ||
                      e.key === "ArrowRight" ||
                      e.key === "/"
                    )
                  ) {
                    e.preventDefault();
                  }
                }}
                required
                placeholder="Data fim:"
                data-tip="Formato DD/MM/AAAA"
                name="dataFim"
                value={formValues.dataFim}
                onChange={handleInputChange}
              />
            </div>
            <div className="input-container">
            <input
              className="campo-form2"
              type="text"
              placeholder="Horário:"
              name="horario"
              value={formValues.horario}
              onChange={handleInputChange}
              required
            />

            <input
              className="campo-form2"
              type="number"
              step="0.01"
              placeholder="Preço:"
              name="preco"
              value={formValues.preco}
              onChange={handleInputChange}
              required
            />
          </div>
          </div>
          <div className="block">
            <h2>Arquivos</h2>
            {/* Restante do código para a seção de arquivos */}
          </div>
          <div className="block">
            <h2>Criar consulta:</h2>
            <button className="circle4-button" onClick={closeWidget}>&gt;</button>
          </div>
        </div>
      </div>
    </article>
  );
}

export default Widget;
