import React, { useState } from "react";
import { useHistory } from "react-router-dom";
import "./Login.css";
import logo from './Imagens/Log.png';
import './Fonts.css';





  function Login() {
      const [username, setUsername] = useState("");
      const [password, setPassword] = useState("");

      const history = useHistory(); // adicionado aqui

      function handleLogin() {
        // aqui você pode validar o login do usuário e, se estiver correto, navegar para a próxima página
        history.push("/Inicio"); // redirecionar para a outra página após o login bem-sucedido
      }
        
      function handleForgotPassword() {
        // Lógica para recuperar a senha
      }
    
      return (
          
        <div className="login-container">
          <div className="login-form">
          <h1>Login</h1>
          <img className="ImagemLogin" src={logo} alt="Imagem de Login" />
          <div className="campos1-form">
            <input
              type="text"
              id="username"
              value={username}
              placeholder="Usuário:"
              onChange={(e) => setUsername(e.target.value)}
            />
            </div>
            <div className="campos2-form">
            <input
              type="password"
              id="password"
              value={password}
              placeholder="Senha:"
              onChange={(e) => setPassword(e.target.value)}
            />
            </div>
          



            <button className="login-button" onClick={handleLogin}>ENTRAR</button>
            <button className="link-button" onClick={handleForgotPassword}>Esqueci minha senha</button>
          </div>
        </div>
      );
    }
    
    export default Login;
