import React, { useState } from "react";
import moment from "moment";
import "./Calendar.css";
import "moment/locale/pt-br";
import img1 from "./Imagens/prox.png";
import img2 from "./Imagens/ant.png";
moment.locale("pt-br");

function getDaysInMonth(month, year) {
  const date = moment(`${year}-${month}-01`);
  const weeks = [];

  while (date.month() === month - 1) {
    const week = [];
    for (let i = 0; i < 7; i++) {
      week.push(date.clone());
      date.add(1, "day");
    }
    weeks.push(week);
  }

  return weeks;
}

const Calendar = ({ colorCode }) => {
  const [currentMonth, setCurrentMonth] = useState(moment());
  const weeks = getDaysInMonth(currentMonth.month() + 1, currentMonth.year());
  const colorCode2 = 2;

  function previousMonth() {
    setCurrentMonth((prevMonth) => prevMonth.clone().subtract(1, "month"));
  }

  function nextMonth() {
    setCurrentMonth((prevMonth) => prevMonth.clone().add(1, "month"));
  }

  const button1Classes = `button1 ${colorCode === 0 ? "green" : colorCode === 1 ? "gray" : "pink"}`;
  const button2Classes = `button1 ${colorCode2 === 0 ? "green" : colorCode2 === 1 ? "gray" : "pink"}`;

  return (
    <div className="calendar-container">
      <div className="calendar-header">
        <button className="circle-button">Nova Consulta</button>
        <button className="circle2-button">+</button>
        <button className="botMes" onClick={previousMonth}>
          <img src={img2} alt="proximo" />
        </button>
        <h1 className="titMes">{currentMonth.format("MMMM YYYY").toUpperCase()}</h1>
        <button className="botMes" onClick={nextMonth}>
          <img src={img1} alt="proximo" />
        </button>
      </div>
      <table className="calendar-table">
        <tbody>
          {weeks.map((week, index) => (
            <tr key={index}>
              {week.map((day) => {
                const isCurrentDay = day.isSame(moment(), "day");
                const isNextMonth = day.month() !== currentMonth.month();
                const dayClasses = isCurrentDay
                  ? "BlocoDia current-day"
                  : isNextMonth
                  ? "BlocoDia cinza"
                  : "BlocoDia responsive-day";

                return (
                  <td className={dayClasses} key={day.format("D MMMM YYYY")}>
                    <div>
                      <div className="DataDia">{day.format("ddd").toUpperCase()}</div>
                      <div>{day.format("D")}</div>
                    </div>
                    <div>
                      <button className={button1Classes}>
                        <h1 className="Titulo2bnt">Fernanda Andrade da Silva</h1>
                      </button>
                      <button className={button2Classes}>
                        <h1 className="Titulo2bnt">Guilherme Fernandes</h1>
                      </button>
                      <button className="buttonVerTodos">Mostrar Todos</button>
                    </div>
                  </td>
                );
              })}
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default Calendar;
