import React, { useState, useEffect } from 'react';
import "./Inicio.css";
import Menu from "../../Menu";
import Header from '../../Header';
import Calendar from './Calendar';
import axios from 'axios';
import "../../Fonts.css";

const Inicio = () => {
  const colorCode = 2;
  const [appointments, setAppointments] = useState([]);
  const crp = "12398472633";
  useEffect(() => {
    const handleTouchMove = (event) => {
      if (event.touches.length > 1) {
        event.preventDefault(); // Impede o comportamento padrão do gesto de zoom
      }
    };

    document.addEventListener("touchmove", handleTouchMove, { passive: false });

    return () => {
      document.removeEventListener("touchmove", handleTouchMove);
    };
  }, []);






  useEffect(() => {
    axios.get(`http://192.168.0.9:8080/appointment/${crp}`)
      .then(response => {
        console.log('Status code:', response.status);
        console.log('Response body:', response.data);
        setAppointments(response.data.object);
      })
      .catch(error => {
        console.error(error);
      });
  }, []);

  return (
    <div className="container">
      <div className="item item1-1" style={{ width: '93%' }}><Menu /></div>
      <div className="item item1-2"><Header/></div>
      <h1>Gerenciamento de Formulários</h1>
      <div className="item item2-1"><Calendar colorCode={colorCode} appointments={appointments} /></div>
    </div>
  );
};

export default Inicio;
