import React, { useState, useEffect, useContext } from 'react';
import { useLocation } from 'react-router-dom';
import { UserContext } from './UserContext';
import "./Inicio.css";
import Menu from "../../Menu";
import Header from '../../Header';
import Calendar from './Calendar';
import axios from 'axios';
import "../../Fonts.css";

const Inicio = (props) => {
  const colorCode = 2;
  const [appointments, setAppointments] = useState([]); 
  const [profissional, setProfissional] = useState([]);
  const location = useLocation();
  const email = location.state.email;
  const { setNome } = useContext(UserContext);
   // Update this line
  useEffect(() => {
    axios.get(`http://192.168.0.9:8080/professional/${email}`)
      .then(response => {
        console.log('Status code:', response.status);
        console.log('Response body:', response.data);        
        setProfissional(response.data.object);
        setNome(profissional.name);
      })
      .catch(error => {
        console.error(error);
      });
  }, [email, setNome, profissional]); // Add email to the dependency array

  useEffect(() => {
    if (profissional && profissional.crp) {
      axios.get(`http://192.168.0.9:8080/appointment/${profissional.crp}`)
        .then(response => {
          console.log('Status code:', response.status);
          console.log('Response body:', response.data);
          setAppointments(response.data.object);
        })
        .catch(error => {
          console.error(error);
        });
    }
  }, [profissional]);

  if (!profissional) {
    // Data is still loading, show a loading spinner or message
    return <div>Loading...</div>;    
  }

  return (
    <div className="container">
      <div className="item item1-1" style={{ width: '93%' }}><Menu /></div>
      <div className="item item1-2"><Header/></div>
      <div className="item item2-1"><Calendar colorCode={colorCode} appointments={appointments} /></div>
    </div>
  );
};

export default Inicio;
