import React, { useState, useEffect } from "react";
import moment from "moment";
import "./Calendar.css";
import Widget from "../widgets/widget-2/Widget";
import "moment/locale/pt-br";
import img1 from "../../Imagens/prox.png";
import img2 from "../../Imagens/ant.png";
moment.locale("pt-br");

function getDaysInMonth(month, year) {
  const date = moment(`${year}-${month}-01`);
  const weeks = [];

  while (date.month() === month - 1) {
    const week = [];
    for (let i = 0; i < 7; i++) {
      week.push(date.clone());
      date.add(1, "day");
    }
    weeks.push(week);
  }

  return weeks;
}

function getWeekDays(startOfWeek) {
  const days = [];
  const date = moment(startOfWeek);

  for (let i = 0; i < 7; i++) {
    days.push(date.clone());
    date.add(1, "day");
  }

  return days;
}

const Calendar = ({ colorCode }) => {
  const [currentMonth, setCurrentMonth] = useState(moment());
  const weeks = getDaysInMonth(currentMonth.month() + 1, currentMonth.year());
  const colorCode2 = 2;
  const [currentWeek, setCurrentWeek] = useState(moment().startOf("week"));
  const [showWidget, setShowWidget] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  const checkIsMobile = () => {
    const isMobile = window.innerWidth <= 768;
    setIsMobile(isMobile);
  };


  useEffect(() => {
    // Função de callback para fechar o widget
    const handleClickOutsideWidget = (event) => {
      // Verifica se o clique foi fora do widget
      if (showWidget && !event.target.closest(".widget-container")) {
        setShowWidget(false);
      }
    };

    // Adiciona o event listener ao clicar fora do widget
    document.addEventListener("click", handleClickOutsideWidget);

    // Remove o event listener ao desmontar o componente
    return () => {
      document.removeEventListener("click", handleClickOutsideWidget);
    };
  }, [showWidget]);


  useEffect(() => {
    checkIsMobile();
    window.addEventListener("resize", checkIsMobile);
    return () => {
      window.removeEventListener("resize", checkIsMobile);
    };
  }, []);

  function previousWeek() {
    setCurrentWeek((prevWeek) => prevWeek.clone().subtract(1, "week"));
  }

  function nextWeek() {
    setCurrentWeek((prevWeek) => prevWeek.clone().add(1, "week"));
  }

  function toggleWidget() {
    setShowWidget((prevState) => !prevState);
  }

  const weekDays = getWeekDays(currentWeek);

  function previousMonth() {
    setCurrentMonth((prevMonth) => prevMonth.clone().subtract(1, "month"));
  }

  function nextMonth() {
    setCurrentMonth((prevMonth) => prevMonth.clone().add(1, "month"));
  }

  const button1Classes = `button1 ${
    colorCode === 0 ? "green" : colorCode === 1 ? "gray" : "pink"
  }`;
  const button2Classes = `button1 ${
    colorCode2 === 0 ? "green" : colorCode2 === 1 ? "gray" : "pink"
  }`;

  return (
    <>
      <div className="calendar-header">                
        {isMobile && (
          <>  
          <button className="circle-button2" onClick={toggleWidget}>
          Nova Consulta
        </button>       
            <button className="botMes" onClick={previousWeek}>
              <img src={img2} alt="proximo" />
            </button>
            <h1 className="titMes2">{`${weekDays[0].format("DD")} a ${weekDays[6].format("DD MMMM YYYY").toUpperCase()}`}</h1>
            <button className="botMes" onClick={nextWeek}>
              <img src={img1} alt="proximo" />
            </button>                   
            {showWidget && (<div className="widget-container2"><Widget /></div>)}   
          </>
          
        )}
         {!isMobile &&(
          <>      
           <button className="circle-button" onClick={toggleWidget}>
          Nova Consulta
        </button>
          <button className="botMes" onClick={previousMonth}>
            <img src={img2} alt="proximo" />
          </button>
          <h1 className="titMes">{currentMonth.format("MMMM YYYY").toUpperCase()}</h1>
          <button className="botMes" onClick={nextMonth}>
            <img src={img1} alt="proximo" />
          </button>          
          {showWidget && (<div className="widget-container"><Widget /></div>)}
        </>
        )}
      </div>

      {!isMobile ? (
        
        <table className="calendar-table">         
          <tbody>
            {weeks.map((week, index) => (
              <tr key={index}>
                {week.map((day) => {
                  const isCurrentDay = day.isSame(moment(), "day");
                  const isNextMonth = day.month() !== currentMonth.month();
                  const dayClasses = isCurrentDay
                    ? "BlocoDia current-day"
                    : isNextMonth
                    ? "BlocoDia cinza"
                    : "BlocoDia";

                  return (
                    <td className={dayClasses} key={day.format("D MMMM YYYY")}>
                      <div>
                        <div className="DataDia">
                          {day.format("ddd").toUpperCase()}
                        </div>
                        <div className="DataDia2">{day.format("D")}</div>
                      </div>
                      <div>
                        <button className={button1Classes}>
                          <h1 className="Titulo2bnt">Fernanda Andrade da Silva</h1>
                        </button>
                        <button className={button2Classes}>
                          <h1 className="Titulo2bnt">Guilherme Fernandes</h1>
                        </button>
                        <button className="buttonVerTodos">Mostrar Todos</button>
                      </div>
                    </td>
                  );
                })}
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        
        <div className="calendar-days-container">          
          {weekDays.map((day, index) => (
            <div className="calendar-day" key={index}>
              <div className="DataDia">{day.format("ddd").toUpperCase()}</div>
              <div className="DataDia2">{day.format("D")}</div>
              <div>
                <button className={button1Classes}>
                  <h1 className="Titulo2bnt2">Fernanda Andrade da Silva</h1>
                </button>
                <button className={button2Classes}>
                  <h1 className="Titulo2bnt2">Guilherme Fernandes</h1>
                </button>
                <button className="buttonVerTodos">Mostrar Todos</button>
              </div>
            </div>
          ))}
        </div>
      )}
    </>
  );
};

export default Calendar;
