import React, { useState } from 'react';
import moment from 'moment';
import 'moment/locale/pt-br';
moment.locale('pt-br');

function getDaysInMonth(month, year) {
  const date = moment(`${year}-${month}-01`);
  const weeks = [];

  while (date.month() === month - 1) {
    const week = [];
    for (let i = 0; i < 7; i++) {
      week.push(date.clone());
      date.add(1, 'day');
    }
    weeks.push(week);
  }

  return weeks;
}

const Calendar = () => {
  const [currentMonth, setCurrentMonth] = useState(moment());
  const weeks = getDaysInMonth(currentMonth.month() + 1, currentMonth.year());

  function previousMonth() {
    setCurrentMonth(prevMonth => prevMonth.clone().subtract(1, 'month'));
  }

  function nextMonth() {
    setCurrentMonth(prevMonth => prevMonth.clone().add(1, 'month'));
  }

  return (
    <React.Fragment>
      <div className="calendar-header">
        <button onClick={previousMonth}>Anterior</button>
        <h1>{currentMonth.format('MMMM YYYY')}</h1>
        <button onClick={nextMonth}>Pr√≥ximo</button>
      </div>
      <table>
        <tbody>
          {weeks.map((week, index) => (
            <tr key={index}>
              {week.map((day) => (
                <td key={day.format('D MMMM YYYY')}>
                  <div>
                    <div>{day.format('ddd')}</div>
                    <div>{day.format('D')}</div>
                  </div>
                  <div>
                    <h2>Title 1</h2>
                    <h2>Title 2</h2>
                    <button>Button 1</button>
                    <button>Button 2</button>
                  </div>
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    </React.Fragment>
  );
};


export default Calendar;
