import React, { useState } from 'react';
import "./Inicio.css";
import Menu from "../../Menu";
import Header from '../../Header';
import moment from 'moment';
import 'moment/locale/pt-br';
moment.locale('pt-br');

function getDaysInMonth(month, year) {
  const date = moment(`${year}-${month}-01`);
  const days = [];

  while (date.month() === month - 1) {
    days.push(date.clone());
    date.add(1, 'day');
  }

  return days;
}

const Inicio = () => {
  const [currentMonth, setCurrentMonth] = useState(moment());
  const days = getDaysInMonth(currentMonth.month() + 1, currentMonth.year());

  function previousMonth() {
    setCurrentMonth(prevMonth => prevMonth.clone().subtract(1, 'month'));
  }

  function nextMonth() {
    setCurrentMonth(prevMonth => prevMonth.clone().add(1, 'month'));
  }

  // Cria uma matriz de semanas e agrupa os dias do mês em suas respectivas semanas
  const weeks = days.reduce((acc, day) => {
    const weekIndex = day.week();
    if (!acc[weekIndex]) {
      acc[weekIndex] = [];
    }
    acc[weekIndex].push(day);
    return acc;
  }, []);

  return (
    <div className="container">
      <div className="item item1-1"><Menu /></div>
      <div className="item item1-2"><Header/></div>
      <div className="item item2">
        <div className="calendar-header">
          <button onClick={previousMonth}>Anterior</button>
          <h1>{currentMonth.format('MMMM YYYY')}</h1>
          <button onClick={nextMonth}>Próximo</button>
        </div>
        <table>
          <thead>
            <tr>
              <th>Domingo</th>
              <th>Segunda</th>
              <th>Terça</th>
              <th>Quarta</th>
              <th>Quinta</th>
              <th>Sexta</th>
              <th>Sábado</th>
            </tr>
          </thead>
          <tbody>
            {weeks.map((week) => (
              <tr key={week[0].format('W YYYY')}>
                {week.map((day) => (
                  <td key={day.format('D MMMM YYYY')}>
                    <div className="day-cell">
                      <div className="day-number">{day.format('D')}</div>
                      <div className="day-content">
                        <h2>Title 1</h2>
                        <h2>Title 2</h2>
                        <button>Button 1</button>
                        <button>Button 2</button>
                      </div>
                    </div>
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

export default Inicio;
