import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Login.css";
import logo from "../../Imagens/Log.png";
import "../../Fonts.css";
import axios from 'axios';

function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [authenticated, setAuthenticated] = useState(false);
  const [error, setError] = useState("");

  const navigate = useNavigate();

  function handleLogin() {
    // Lógica de autenticação do usuário aqui

    const requestBody = {
      email: username,
      password: password
    };

    const url = 'http://localhost:8080/professional/authentication';

    axios.post(url, requestBody)
      .then(response => {
        console.log('Status code:', response.status);
        console.log('Response body:', response.data);

        if (response.status === 200) {
          // Autenticação bem-sucedida
          setAuthenticated(true);
          // Navegue para a tela Inicio
          navigate("/Inicio");
        } else {
          setError("Credenciais inválidas");
        }
      })
      .catch(error => {
        console.error('Error:', error);
      });
  }

  function handleCriarConta() {
    if (authenticated) {
      console.log("Você já está autenticado");
      return;
    }
    else{
      navigate("/CriarConta");
    }
      
    }


  function handleForgotPassword() {
    // Lógica para recuperar a senha
  }

  return (
    <div className="login-container">
      <div className="login-form">
        <h1>Login</h1>
        <img className="ImagemLogin" src={logo} alt="Imagem de Login" />
        <div className="campos1-form">
          <input
            type="text"
            id="username"
            value={username}
            placeholder="Usuário:"
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className="campos2-form">
          <input
            type="password"
            id="password"
            value={password}
            placeholder="Senha:"
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button className="login-button" onClick={handleLogin}>
          ENTRAR
        </button>
        {error && <p className="error-message">{error}</p>}
        <button className="link-button" onClick={handleForgotPassword}>
          Esqueci minha senha
        </button>
        <button className="Criar-button" onClick={handleCriarConta}>
          Criar uma nova conta
        </button>
        
      </div>
    </div>
  );
}

export default Login;
