import React, { useState, useEffect } from "react";
import "./Inicio.css";
import Menu from "../../Menu";
import Header from '../../Header';
import Calendar from './Calendar';
import "../../Fonts.css";
// ...código anterior omitido para concisão

const Inicio = () => {
  const colorCode = 0; // O valor de colorCode será alterado conforme situação da consulta (0 / verde = confirmada, 1 / cinza = pendente e 2 / rosa = cancelada)
  useEffect(() => {
    // Função de callback para fechar o widget
    const handleClickOutsideWidget = (event) => {
      // Verifica se o clique foi fora do widget
      if (showWidget && !event.target.closest(".widget-container")) {
        setShowWidget(false);
      }
    };

    // Adiciona o event listener ao clicar fora do widget
    document.addEventListener("click", handleClickOutsideWidget);

    // Remove o event listener ao desmontar o componente
    return () => {
      document.removeEventListener("click", handleClickOutsideWidget);
    };
  }, [showWidget]);
  return (
    <div className="container">
      <div className="item item1-1" style={{ width: '93%' }}><Menu /></div>
      <div className="item item1-2"><Header/></div>
      <div className="item item2-1"><Calendar colorCode={colorCode} />
      </div>
      
    </div>
  );
};

export default Inicio;
