import { useRef, useState } from "react";
import "./styles.css";

const menuHeight = getComputedStyle(document.documentElement).getPropertyValue(
  "--menu-height"
);

const buttons = ["Dados do Paciente", "Dados da Consulta", "Arquivos", "Finalizar"];

function Widget () {
  const [activeBlock, setActiveBlock] = useState(0);

  const toggleMenuBlock = (index) => {
    setActiveBlock(index);
  };

  return (
    <article className={`card ${activeBlock === 0 ? "open" : ""}`}>
      <div className="buttons">
        {buttons.map((button, index) => (
          <button
            className={index === activeBlock ? "active" : ""}
            onClick={() => toggleMenuBlock(index)}
          >
            {button}
          </button>
        ))}
      </div>
      <div className="wrapper">
        <div
          className="content"
          style={{ transform: `translate(0, calc(0px - ${menuHeight} * ${activeBlock}))` }}
        >
          <div className="block">
            <h2>Dados do Paciente</h2>
            <input className="campo-form" type="text" placeholder="Nome: " />
            <input className="campo-form" type="text" placeholder="CPF:" />
            <input className="campo-form" type="text" placeholder="E-mail:" />
            <button className="circle3-button" >&gt;</button>
          </div>
          <div className="block">
            <h2>Dados da Consulta</h2>
            <p>
              Vivamus volutpat ipsum ac ipsum feugiat, vel molestie elit
              vestibulum. Donec luctus commodo dictum. Aenean in turpis erat.
              Vestibulum imperdiet nibh. Ipsum ac ipsum feugiat, vel molestie.
            </p>
          </div>
          <div className="block">
            <h2>Arquivos Relacionados</h2>
            <p>
              Vivamus volutpat ipsum ac ipsum feugiat, vel molestie elit
              vestibulum. Donec luctus commodo dictum. Aenean in turpis erat.
              Vestibulum imperdiet nibh. Ipsum ac ipsum feugiat, vel molestie.
            </p>
          </div>
          <div className="block">
            <h2>Deseja confirmar a consulta?</h2>
            <button className="circle3-button">&gt;</button>
          </div>
        </div>
      </div>
    </article>
  );
};

export default Widget;
