import React, { useState } from "react";
import img1 from "../../Imagens/folder-open.png";
import "../../Fonts.css";
import "./Arquivos.css";

const FileList = ({ files, onDelete, onView }) => {
  return (
    <div className="file-container">
      {files.map((file) => (
        <div className="file-button" key={file.id} onClick={() => onView(file)}>
          <img src={img1} alt="File Icon" />
          <span className="nomeArq">{file.name}</span>
          <button className="botAcao" onClick={() => onView(file)}>Abrir</button>
          <button className="botAcao" onClick={() => onDelete(file.id)}>Excluir</button>
        </div>
      ))}
    </div>
  );
};

const FileUploader = ({ onUpload }) => {
  const [selectedFile, setSelectedFile] = useState(null);

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  const handleUpload = () => {
    if (selectedFile) {
      const fileData = {
        id: Date.now(),
        name: selectedFile.name,
        file: selectedFile,
      };
      onUpload(fileData);
      setSelectedFile(null);
    }
  };

  return (
    <div>
      <input type="file" onChange={handleFileChange} className="file-input" />
      <button className="botAdicionar" onClick={handleUpload}>Adicionar</button>
    </div>
  );
};

const Arquivos = () => {
  const [files, setFiles] = useState([]);
  const [selectedFile, setSelectedFile] = useState(null);

  const handleUpload = (file) => {
    setFiles((prevFiles) => [...prevFiles, file]);
  };

  const handleDelete = (fileId) => {
    setFiles((prevFiles) => prevFiles.filter((file) => file.id !== fileId));
  };

  const handleView = (file) => {
    setSelectedFile(file);
  };

  const handleBack = () => {
    setSelectedFile(null);
  };

  return (
    <div>
      <h1>Gerenciamento de Formulários</h1>
      {selectedFile ? (
        <div>
          <h2>Visualização de Arquivo: {selectedFile.name}</h2>
          <button className="botVoltar" onClick={handleBack}>Voltar</button>
          <div className="file-button">
            <img src={img1} alt="File Icon" />            
          </div>
        </div>
      ) : (
        <div>
          <FileList files={files} onDelete={handleDelete} onView={handleView} />
          <FileUploader onUpload={handleUpload} />
        </div>
      )}
    </div>
  );
};

export default Arquivos;
