import React from 'react';
import { Link } from 'react-router-dom';
import "./Menu.css"; 
import consulta from "./Imagens/consulta.png";
import financeiro from "./Imagens/Financeiro.png";
import formulario from "./Imagens/Formulario.png";
import paciente from "./Imagens/Paciente.png";
import imagem1 from "./Imagens/Logado.png";
import { useNavigate } from "react-router-dom";
import moment from 'moment';

const Menu = () => {


  const navigate = useNavigate();
  function handleLogin() {
    // Lógica de autenticação do usuário aqui
    
    navigate("/");
  }

  const agora = moment();
  const hora = agora.hour();
  let saudacao = '';

  if (hora >= 5 && hora < 12) {
    saudacao = 'Bom dia';
  } else if (hora >= 12 && hora < 18) {
    saudacao = 'Boa tarde';
  } else {
    saudacao = 'Boa noite';
  }



  return (
    <div>
    <div className="menu-lateral">
      <h1 className="sidebar-title">MIND TRACKER</h1>
      <Link to="/Inicio" className="menu-button"><img src={consulta} alt="Descrição da imagem" style={{maxWidth: "20%", verticalAlign: "middle"}} />Consultas</Link>
      <Link to="/Formularios" className="menu-button"><img src={formulario} alt="Descrição da imagem" style={{maxWidth: "20%", verticalAlign: "middle"}}/>Formulários</Link>
      <Link to="/Financeiro" className="menu-button"><img src={financeiro} alt="Descrição da imagem" style={{maxWidth: "20%", verticalAlign: "middle"}}/>Financeiro</Link>
      <Link to="/Pacientes" className="menu-button"><img src={paciente} alt="Descrição da imagem" style={{maxWidth: "20%", verticalAlign: "middle"}}/>Pacientes</Link>
    </div>
    <div className="MenuDireito">
        <div className="Titulos">        
          <h1 className="title1">{saudacao}</h1>
          <h1 className="title2">Lídia Ribeiro</h1>
        </div>
          <button className="img-button" onClick={handleLogin}><img src={imagem1} alt="Descrição da imagem" /></button> 
      </div>
    </div>
  );
};
export default Menu;
