import React from 'react';
import moment from 'moment';

function getDaysInMonth(month, year) {
  const date = moment(`${year}-${month}-01`);
  const days = [];

  while (date.month() === month - 1) {
    days.push(date.clone());
    date.add(1, 'day');
  }

  return days;
}

function Calendar() {
  const days = getDaysInMonth(moment().month() + 1, moment().year());

  return (
    <div>
      <h1>{moment().format('MMMM YYYY')}</h1>
      <table>
        <thead>
          <tr>
            <th>Sun</th>
            <th>Mon</th>
            <th>Tue</th>
            <th>Wed</th>
            <th>Thu</th>
            <th>Fri</th>
            <th>Sat</th>
          </tr>
        </thead>
        <tbody>
          {days.map((day) => (
            <td>{day.format('D')}</td>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Calendar;
