import React, { useState, useMemo, useEffect, useContext } from "react";
import { UserContext } from "./../../UserContext";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import './Pacientes.css';
import Menu from '../../Menu';
import Header from '../../Header';
import WidgetPac from './WidgetPac'; // Importar o componente Widget
import visualizar from "../../Imagens/visualizar.png";
import editar from "../../Imagens/editar.png";
import excluir from "../../Imagens/trash.png";
import { useTable, useFilters } from 'react-table';

const Pacientes = () => {
  const [valueInput, setValueInput] = useState('');
  const [filteredData, setFilteredData] = useState([]);
  const [pacientes, setPacientes] = useState([]);
  const [nomes, setNomes] = useState([]);
  const { crp } = useContext(UserContext);
  const [showWidget, setShowWidget] = useState(false);
  const [selectedPaciente, setSelectedPaciente] = useState(null); // Novo estado para o paciente selecionado
  const navigate = useNavigate();
  const handleValueChange = (e) => {
    const value = e.target.value || '';
    setValueInput(value);

    const filtered = pacientes.filter((item) => item.name.toLowerCase().includes(value.toLowerCase()));
    setFilteredData(filtered);
  };

  const columns = useMemo(
    () => [
      {
        Header: 'Nome',
        accessor: 'name',
      },
      {
        Header: 'Email',
        accessor: 'emailAddress',
      },
      {
        Header: 'Telefone',
        accessor: 'phoneNumber',
      },
      {
        Header: 'Ação',
        accessor: 'acao',
        Cell: ({ row }) => (
          <div>
            <button style={{ backgroundColor: "transparent", border: "none", cursor: "pointer"}} onClick={() => handleView(row)}><img src={visualizar} style={{ width: "80%" }} alt="Descrição da imagem"/></button>
            <button style={{ backgroundColor: "transparent", border: "none", cursor: "pointer"}} onClick={() => handleEdit(row)}><img src={editar} style={{ width: "80%" }} alt="Descrição da imagem"/></button>
            <button style={{ backgroundColor: "transparent", border: "none", cursor: "pointer"}} onClick={() => handleDelete(row)}><img src={excluir} style={{ width: "80%" }} alt="Descrição da imagem"/></button>
          </div>
        ),
      },
    ],
    []
  );

  function CadastrarPac() {    
    navigate("/CriarPac");
  }

  const { getTableProps, getTableBodyProps, headerGroups, rows, prepareRow } = useTable(
    {
      columns,
      data: filteredData,
    },
    useFilters
  );

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://192.168.0.9:8080/professional/patients/${crp}`);
        const pacientes = response.data.object;
        setPacientes(pacientes);
        const names = pacientes.map((item) => item.name);
        names.sort();
        if (Array.isArray(names)) {
          setNomes(names);
        } else {
          console.error("Os dados obtidos não são um array:", names);
        }
      } catch (error) {
        console.error("Erro ao obter os nomes da base de dados:", error);
      }
    };

    fetchData();
  }, [crp]);

  useEffect(() => {
    setFilteredData(pacientes);
  }, [pacientes]);

  const handleView = (row) => {
    setSelectedPaciente(row.original);
    setShowWidget(true);
  };

  const handleEdit = (row) => {
    // Implement your logic to handle the edit action here
    // You can access the row data using row.original
    console.log("Edit row:", row.original);
  };

  const handleDelete = (row) => {
    // Implement your logic to handle the delete action here
    // You can access the row data using row.original
    console.log("Delete row:", row.original);
  };

  return (
    <div className="container">
      {/* ... */}
      <div className="item itemP2-1">      
        <h1 className="tituloprinc">Gerenciamento de Pacientes</h1>
        <div className="input-container" style={{marginTop:"3%"}}>          
          {/* ... */}
        </div>
        <table style={{ width: '100%' }} className="tableP" {...getTableProps()}>
          {/* ... */}
        </table>
      </div>
      {/* Render the widget if showWidget is true */}
      {showWidget && (
        <div className="widget-container">
          {/* Pass the selectedPaciente as a prop to the widget */}
          <WidgetPac paciente={selectedPaciente} onClose={() => setShowWidget(false)} />
        </div>
      )}
    </div>
  );
};

export default Pacientes;
