import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Login.css";
import logo from "../../Imagens/Log.png";
import "../../Fonts.css";
import axios from 'axios';


function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [authenticated, setAuthenticated] = useState(false);
  const navigate = useNavigate();

  function handleLogin() {
    // Lógica de autenticação do usuário aqui
  
    // Verificar se as credenciais são válidas
    if (username === "admin" && password === "password") {
      // Autenticação bem-sucedida
      setAuthenticated(true);
      // Navegue para a tela Inicio
      navigate("/Inicio");
    } else {
      // Credenciais inválidas, exiba uma mensagem de erro ou tome alguma ação apropriada
      console.log("Credenciais inválidas");
    }
  }
  

  function handleCriarConta() {
    if (authenticated) {
      console.log("Você já está autenticado");
      return;
    }
    else{
      const url = 'http://localhost:8080/professional';
      const requestBody = {
        crp: '12398472633',
        name: 'testando insert prof',
        phoneNumber: '2198477650',
        dateOfBirth: '15/09/1993',
        emailAddress: 'testandoCadastroProf@gmail.com',
        city: 'Higienopolis',
        state: 'Sao Paulo',
        country: 'teste update address',
        street: 'rua 12',
        postalCode: '30219011',
        password: '123'
      };
    
      axios.post(url, requestBody)
        .then(response => {
          console.log('Status code:', response.status);
          console.log('Response body:', response.data);
        })
        .catch(error => {
          console.error('Error:', error);
        });
    }
      
    }


  function handleForgotPassword() {
    // Lógica para recuperar a senha
  }

  return (
    <div className="login-container">
      <div className="login-form">
        <h1>Login</h1>
        <img className="ImagemLogin" src={logo} alt="Imagem de Login" />
        <div className="campos1-form">
          <input
            type="text"
            id="username"
            value={username}
            placeholder="Usuário:"
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className="campos2-form">
          <input
            type="password"
            id="password"
            value={password}
            placeholder="Senha:"
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>
        <button className="login-button" onClick={handleLogin}>
          ENTRAR
        </button>
        <button className="link-button" onClick={handleForgotPassword}>
          Esqueci minha senha
        </button>
        <button className="Criar-button" onClick={handleCriarConta}>
          Criar uma nova conta
        </button>
        
      </div>
    </div>
  );
}

export default Login;
