import React, { useState } from 'react';
import moment from 'moment';
import 'moment/locale/pt-br';
moment.locale('pt-br');

function getDaysInMonth(month, year) {
  const date = moment(`${year}-${month}-01`);
  const days = [];

  while (date.month() === month - 1) { // Enquanto o mês for o mesmo, continua adicionando ao array
    days.push(date.clone()); // Usando clone() para criar um novo objeto moment.js para cada dia
    date.add(1, 'day'); // Adiciona um dia à data
  }

  return days;
}

const Calendar = () => {
  const [currentMonth, setCurrentMonth] = useState(moment()); // Armazenando o mês atual usando useState
  const days = getDaysInMonth(currentMonth.month() + 1, currentMonth.year()); // Obtendo os dias do mês atual
  
  // Função para ir para o mês anterior
  function previousMonth() {
    setCurrentMonth(prevMonth => prevMonth.clone().subtract(1, 'month')); // Setando o novo estado usando setState e moment.js
  }

  // Função para ir para o próximo mês
  function nextMonth() {
    setCurrentMonth(prevMonth => prevMonth.clone().add(1, 'month')); // Setando o novo estado usando setState e moment.js
  }
  
  return (
    <React.Fragment>
      <div className="calendar-header">
        <button onClick={previousMonth}>Anterior</button> {/* Botão para ir para o mês anterior */}
        <h1>{currentMonth.format('MMMM YYYY')}</h1> {/* Exibindo o mês atual */}
        <button onClick={nextMonth}>Próximo</button> {/* Botão para ir para o próximo mês */}
      </div>
      <table>
        <tbody>
          {days.map((day) => (
            <tr key={day.format('D MMMM YYYY')}>
              <td>{day.format('ddd')}</td> {/* Exibe o dia da semana (3 primeiras letras) */}
              <td>{day.format('D')}</td> {/* Exibe o número do dia */}
              <td>
                <h2>Title 1</h2> {/* Título 1 */}
                <h2>Title 2</h2> {/* Título 2 */}
                <button>Button 1</button> {/* Botão 1 */}
                <button>Button 2</button> {/* Botão 2 */}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </React.Fragment>
  );
}

export default Calendar;
