import React from 'react';
import "./Header.css"; 
import imagem1 from "./Imagens/Logado.png";
import imagem2 from "./Imagens/Pesquisa.png"
import { useNavigate } from "react-router-dom";
import moment from 'moment';


const Header = () => {

    const navigate = useNavigate();

  
    function handleLogin() {
      // Lógica de autenticação do usuário aqui
      navigate("/");
    }
  
    const agora = moment();
    const hora = agora.hour();
    let saudacao = '';
  
    if (hora >= 5 && hora < 12) {
      saudacao = 'Bom dia';
    } else if (hora >= 12 && hora < 18) {
      saudacao = 'Boa tarde';
    } else {
      saudacao = 'Boa noite';
    }

    return (
      <div className="Menusuperior">
        <button className="img2-button" onClick={handleLogin}>
                <img src={imagem2} alt="Descrição da imagem" />
            </button> 

        <div className="MenuDireito">
            <div className="Titulos">        
                <h1 className="title1">{saudacao}</h1>
                <h1 className="title2">Lídia Ribeiro</h1>
            </div>
            <button className="img-button" onClick={handleLogin}>
                <img src={imagem1} alt="Descrição da imagem" />
            </button> 
        </div>
        </div>
    );
}

export default Header;
