import { useState } from "react";
import "./Widget.css";

const menuHeight = getComputedStyle(document.documentElement).getPropertyValue(
  "--menu-height"
);

const buttons = ["Dados do Paciente", "Dados da Consulta", "Arquivos", "Finalizar"];

function Widget () {
  const [activeBlock, setActiveBlock] = useState(0);

  const toggleMenuBlock = (index) => {
    setActiveBlock(index);
  };

  return (
    <article className={`card ${activeBlock === 0 ? "open" : ""}`}>
      <div className="buttons">
        {buttons.map((button, index) => (
          <button
            className={index === activeBlock ? "active" : ""}
            onClick={() => toggleMenuBlock(index)}
          >
            {button}
          </button>
        ))}
      </div>
      <div className="wrapper">
        <div
          className="content"
          style={{ transform: `translate(0, calc(0px - ${menuHeight} * ${activeBlock}))` }}
        >
          <div className="block">
            <h2>Dados do Paciente</h2>
            <input className="campo-form2" type="text" placeholder="Nome: " />
            <input
                className="campo-form2"
                type="text"
                maxLength="11"
                placeholder="CPF (Sem pontos ou traço):"
                pattern="\d{3}\.\d{3}\.\d{3}-\d{2}"
                title="Digite um CPF válido no formato XXXXXXXXXXX"
                onKeyDown={(e) => {
                  if (
                    !((e.key >= "0" && e.key <= "9") || // permite números de 0 a 9
                      e.key === "Backspace" || // permite a tecla Backspace para apagar
                      e.key === "Delete" || // permite a tecla Delete para apagar
                      e.key === "Tab" || // permite a tecla Tab para navegar
                      e.key === "ArrowLeft" || // permite a tecla de seta para a esquerda para navegar
                      e.key === "ArrowRight" // permite a tecla de seta para a direita para navegar
                    )
                  ) {
                    e.preventDefault(); // impede a entrada do caractere não permitido
                  }
                }}
                required
              />

            <input className="campo-form2" type="text" placeholder="E-mail:" />
          </div>
          <div className="block">
            <h2>Dados da Consulta</h2>
            <input className="campo-form2" type="text" placeholder="Data: " />
            <select className="campo-form2" type="text" placeholder="Recorrência:">  
            <option value="" disabled selected>Recorrencia:</option>
            <option value="opcao1">Virtual</option>
            <option value="opcao2">Presencial</option>
            </select> 
            <input className="campo-form2" type="text" placeholder="Local:" />
            <input className="campo-form2" type="text" placeholder="Preço:" />
          </div>
          <div className="block">
            <h2>Arquivos do Paciente</h2>
            <p>

            </p>
          </div>
          <div className="block">
            <h2>Deseja confirmar a consulta?</h2>
            <button className="circle3-button">&gt;</button>
          </div>
        </div>
      </div>
    </article>
  );
};

export default Widget;
