import React, { useState } from "react";
import moment from "moment";
import "./Calendar.css";
import "moment/locale/pt-br";
moment.locale("pt-br");

// Função auxiliar para obter os dias de um determinado mês e ano
function getDaysInMonth(month, year) {
  const date = moment(`${year}-${month}-01`);
  const weeks = [];

  // Enquanto o mês da data for igual ao mês fornecido, itera para obter os dias da semana
  while (date.month() === month - 1) {
    const week = [];
    for (let i = 0; i < 7; i++) {
      week.push(date.clone());
      date.add(1, "day");
    }
    weeks.push(week);
  }

  return weeks;
}

const Calendar = ({ colorCode }) => {
  // Define o estado do mês atual utilizando o hook useState do React
  const [currentMonth, setCurrentMonth] = useState(moment());
  // Obtém as semanas e dias do mês atual chamando a função getDaysInMonth
  const weeks = getDaysInMonth(currentMonth.month() + 1, currentMonth.year());

  // Função para avançar para o mês anterior
  function previousMonth() {
    // Atualiza o estado do mês utilizando o método subtract do Moment.js
    setCurrentMonth((prevMonth) => prevMonth.clone().subtract(1, "month"));
  }

  // Função para avançar para o próximo mês
  function nextMonth() {
    // Atualiza o estado do mês utilizando o método add do Moment.js
    setCurrentMonth((prevMonth) => prevMonth.clone().add(1, "month"));
  }

  // Define as classes CSS dos botões com base no valor de colorCode
  const button1Classes = `button1 ${
    colorCode === 0 ? "green" : colorCode === 1 ? "gray" : "pink"
  }`;

  return (
    <>
      <div className="calendar-header">
        {/* Botão para ir para o mês anterior */}
        <button className="botMes" onClick={previousMonth}>
          Mês Anterior
        </button>
        {/* Exibe o mês atual formatado como "MMMM YYYY" e em caixa alta */}
        <h1>{currentMonth.format("MMMM YYYY").toUpperCase()}</h1>
        {/* Botão para ir para o próximo mês */}
        <button className="botMes" onClick={nextMonth}>
          Próximo Mês
        </button>
        <button className="botMes" onClick={nextMonth}>
          Próximo Mês
        </button>
      </div>
      <table className="calendar-table">
        <tbody>
          {/* Mapeia as semanas e dias do mês para renderizar a tabela do calendário */}
          {weeks.map((week, index) => (
            <tr key={index}>
              {week.map((day) => {
                // Verifica se o dia atual é o dia atual do sistema
                const isCurrentDay = day.isSame(moment(), "day");
                // Verifica se o dia pertence ao mês seguinte
                const isNextMonth = day.month() !== currentMonth.month();
                // Define as classes CSS do dia com base nas condições anteriores
                const dayClasses = isCurrentDay
                  ? "BlocoDia current-day"
                  : isNextMonth
                  ? "BlocoDia cinza"
                  : "BlocoDia";

                return (
                  <td className={dayClasses} key={day.format("D MMMM YYYY")}>
                    <div>
                      <div className="DataDia">
                        {day.format("ddd").toUpperCase()}
                      </div>
                      {/* Exibe o número do dia */}
                      <div>{day.format("D")}</div>
                    </div>
                    <div>
                      {/* Botões adicionais */}
                      <button className={button1Classes}>
                        <h1 className="Titulo2bnt">Fernanda A Silva</h1>
                      </button>
                      <button className={button1Classes}>
                        <h1 className="Titulo2bnt">Fernanda A Silva</h1>
                      </button>
                      <button className="buttonVerTodos">Mostrar Todos</button>
                    </div>
                  </td>
                );
              })}
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
};

export default Calendar;
