import React, { useState } from "react";

// Componente responsável por exibir a lista de arquivos e permitir a exclusão deles
const FileList = ({ files, onDelete }) => {
  return (
    <ul>
      {files.map((file) => (
        <li key={file.id}>
          <span>{file.name}</span>
          <button onClick={() => onDelete(file.id)}>Excluir</button>
        </li>
      ))}
    </ul>
  );
};

// Componente responsável por permitir o upload de arquivos
const FileUploader = ({ onUpload }) => {
  const [selectedFile, setSelectedFile] = useState(null);

  // Função para lidar com a seleção de arquivo
  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
  };

  // Função para lidar com o upload do arquivo selecionado
  const handleUpload = () => {
    if (selectedFile) {
      // Cria um objeto com dados do arquivo selecionado
      const fileData = {
        id: Date.now(),
        name: selectedFile.name,
      };
      // Chama a função de callback para enviar os dados do arquivo
      onUpload(fileData);
      setSelectedFile(null); // Limpa o arquivo selecionado após o upload
    }
  };

  return (
    <div>
      {/* Input para selecionar o arquivo */}
      <input type="file" onChange={handleFileChange} />
      {/* Botão para realizar o upload */}
      <button onClick={handleUpload}>Adicionar</button>
    </div>
  );
};

const Arquivos = () => {
  const [files, setFiles] = useState([]);

  // Função para lidar com o upload de arquivos
  const handleUpload = (file) => {
    // Adiciona o novo arquivo à lista de arquivos
    setFiles((prevFiles) => [...prevFiles, file]);
  };

  // Função para lidar com a exclusão de arquivos
  const handleDelete = (fileId) => {
    // Filtra a lista de arquivos para remover o arquivo com o ID fornecido
    setFiles((prevFiles) => prevFiles.filter((file) => file.id !== fileId));
  };

  return (
    <div>
      <h1>Gerenciamento de Arquivos</h1>
      {/* Componente FileList para exibir a lista de arquivos */}
      <FileList files={files} onDelete={handleDelete} />
      {/* Componente FileUploader para permitir o upload de arquivos */}
      <FileUploader onUpload={handleUpload} />
    </div>
  );
};

export default Arquivos;
