import React, { useState, useEffect, useContext } from 'react';
import { useNavigate } from "react-router-dom";

import "./Login.css";
import logo from "../../Imagens/Log.png";
import ou from "../../Imagens/ou.png";
import "../../Fonts.css";
import axios from 'axios';
import { UserContext } from '../../UserContext';

function Login() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [authenticated, setAuthenticated] = useState(false);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");
  const [isLoading, setIsLoading] = useState(false); // Novo estado para controlar o carregamento
  const navigate = useNavigate();
  const { setEmail } = useContext(UserContext);

  function handleCriarConta() {
    // Lógica para criar uma nova conta
  }

  function handleForgotPassword() {
    // Lógica para recuperar a senha
  }

  function handleLogin() {
    setIsLoading(true); // Exibir tela de carregamento

    console.log("Fazendo login...");
    // Lógica de login
    const requestBody = {
      email: username,
      password: password
    };

    const url = 'http://192.168.0.9:8080/professional/authentication';

    axios.post(url, requestBody)
      .then(response => {
        console.log('Status code:', response.status);
        console.log('Response body:', response.data);

        if (response.status === 200) {
          // Autenticação bem-sucedida
          setAuthenticated(true);
          setError("");
          setSuccessMessage("Login efetuado com sucesso!");
          setTimeout(() => {
            setSuccessMessage("");
            setEmail(username);
            navigate("/Inicio");
          }, 2000);
        } else {
          setError("Credenciais inválidas");
        }
      })
      .catch(error => {
        console.error('Error:', error);
        setError("Erro ao fazer login");
      })
      .finally(() => {
        setIsLoading(false); // Ocultar tela de carregamento, independentemente do resultado
      });
  }

  return (
    <div className="login-container">
      <div className="login-form">
        <h1 className="titlog">LOGIN</h1>
        <img className="ImagemLogin" src={logo} alt="Imagem de Login" />
        <div className="campos1-form">
          <input
            type="text"
            id="username"
            value={username}
            placeholder="Usuário:"
            onChange={(e) => setUsername(e.target.value)}
          />
        </div>
        <div className="campos2-form">
          <input
            type="password"
            id="password"
            value={password}
            placeholder="Senha:"
            onChange={(e) => setPassword(e.target.value)}
          />
        </div>

        {successMessage && <p className="success-message">{successMessage}</p>}
        <button className="login-button" onClick={handleLogin}>
          ENTRAR
        </button>
        <button className="link-button" onClick={handleForgotPassword}>
          Esqueci minha senha
        </button>
        {error && <p className="error-message">{error}</p>}
        <img src={ou} alt="ou" style={{ width: '290px', marginBottom: '2%' }} />

        <button className="Criar-button" onClick={handleCriarConta}>
          Criar uma nova conta
        </button>
      </div>

      {isLoading && ( // Exibir tela de carregamento quando isLoading for true
        <div className="loading-container">
          <p>Carregando...</p>
        </div>
      )}
    </div>
  );
}

export default Login;
