import { useState } from "react";
import "./Widget.css";
import close1 from "../../../Imagens/closeR.png";



const menuHeight = getComputedStyle(document.documentElement).getPropertyValue("--menu-height");

const buttons = ["Dados do Paciente", "Dados da Consulta", "Arquivos", "Finalizar"];

function Widget () {
  const [activeBlock, setActiveBlock] = useState(0);
  const [isWidgetOpen, setIsWidgetOpen] = useState(true);
  const toggleMenuBlock = (index) => {
    setActiveBlock(index);
  };

  const closeWidget = (event) => {
    event.stopPropagation(); // Impede a propagação do evento
    setIsWidgetOpen(false); // Define isWidgetOpen como false para fechar o widget
  };

  if (!isWidgetOpen) {
    return null; // Retorna null para não renderizar o widget quando estiver fechado
  }







  return (
    <article className={`card ${activeBlock === 0 ? "open" : ""}`}>
      <div className="buttons">
  {buttons.map((button, index) => (
    <button
      className={index === activeBlock ? "active" : ""}
      onClick={() => toggleMenuBlock(index)}
      key={index} // Add key prop here with a unique value
    >
      {button}
    </button>
  ))}

</div>

      <div className="wrapper">
      <button className="close-button" onClick={closeWidget}><img src={close1} alt="fechar" /></button> {/* Botão para fechar o widget */}
        <div className="content" style={{ transform: `translate(0, calc(0px - ${menuHeight} * ${activeBlock}))` }}>
          <div className="block">
            <h2>Dados do Paciente</h2>
            <input className="campo-form2" type="text" placeholder="Nome: " />
            <input
                className="campo-form2"
                type="text"
                maxLength="11"
                placeholder="CPF:"
                pattern="\d{3}\.\d{3}\.\d{3}-\d{2}"
                title="Digite um CPF válido no formato XXXXXXXXXXX"
                onKeyDown={(e) => {
                  if (
                    !((e.key >= "0" && e.key <= "9") || // permite números de 0 a 9
                      e.key === "Backspace" || // permite a tecla Backspace para apagar
                      e.key === "Delete" || // permite a tecla Delete para apagar
                      e.key === "Tab" || // permite a tecla Tab para navegar
                      e.key === "ArrowLeft" || // permite a tecla de seta para a esquerda para navegar
                      e.key === "ArrowRight" // permite a tecla de seta para a direita para navegar
                    )
                  ) {
                    e.preventDefault(); // impede a entrada do caractere não permitido
                  }
                }}
                required
              />

            <input className="campo-form2" type="text" placeholder="E-mail:" />
          </div>
          <div className="block">
  <h2>Dados da Consulta</h2>
  <input className="campo-form2" type="text" placeholder="Local:" />

  <select
    className="campo-form2"
    defaultValue=""
    placeholder="Recorrência:"
  >
    <option value="" disabled>
      Recorrência:
    </option>
    <option value="opcao1">Virtual</option>
    <option value="opcao2">Presencial</option>
  </select>

  <div className="input-container">
    <input
      className="campo-form2"
      type="text"
      style={{ width: "0%"}}
      pattern="\d{2}\/\d{2}\/\d{4}"
      onKeyDown={(e) => {
        if (
          !(
            (e.key >= "0" && e.key <= "9") || // permite números de 0 a 9
            e.key === "Backspace" || // permite a tecla Backspace para apagar
            e.key === "Delete" || // permite a tecla Delete para apagar
            e.key === "Tab" || // permite a tecla Tab para navegar
            e.key === "ArrowLeft" || // permite a tecla de seta para a esquerda para navegar
            e.key === "ArrowRight" || // permite a tecla de seta para a direita para navegar
            e.key === "/" // permite a tecla de barra para a data
          )
        ) {
          e.preventDefault(); // impede a entrada do caractere não permitido
        }
      }}
      required
      placeholder="Data início:"
      data-tip= "Formato DD/MM/AAAA"
    />
    <input
      className="campo-form2"
      type="text"
      style={{ width: "50%" }}
      pattern="\d{2}\/\d{2}\/\d{4}"
      onKeyDown={(e) => {
        if (
          !(
            (e.key >= "0" && e.key <= "9") || // permite números de 0 a 9
            e.key === "Backspace" || // permite a tecla Backspace para apagar
            e.key === "Delete" || // permite a tecla Delete para apagar
            e.key === "Tab" || // permite a tecla Tab para navegar
            e.key === "ArrowLeft" || // permite a tecla de seta para a esquerda para navegar
            e.key === "ArrowRight" || // permite a tecla de seta para a direita para navegar
            e.key === "/" // permite a tecla de barra para a data
          )
        ) {
          e.preventDefault(); // impede a entrada do caractere não permitido
        }
      }}
      required
      placeholder="Data fim:"
      data-tip= "Formato DD/MM/AAAA"
    />
  </div>
  <div className="input-container">
  <input
    className="campo-form2"
    type="text"

    pattern="\d{2}\:\d{2}"
    onKeyDown={(e) => {
      if (
        !(
          (e.key >= "0" && e.key <= "9") || // permite números de 0 a 9
          e.key === "Backspace" || // permite a tecla Backspace para apagar
          e.key === "Delete" || // permite a tecla Delete para apagar
          e.key === "Tab" || // permite a tecla Tab para navegar
          e.key === "ArrowLeft" || // permite a tecla de seta para a esquerda para navegar
          e.key === "ArrowRight" || // permite a tecla de seta para a direita para navegar
          e.key === ":" // permite a tecla de barra para a hora
        )
      ) {
        e.preventDefault(); // impede a entrada do caractere não permitido
      }
    }}
    required
    placeholder="Horário:"
    data-tip = "Formato hh/mm"
  />
  <input className="campo-form2" 
  type="text"     
    onKeyDown={(e) => {
      if (
        !(
          (e.key >= "0" && e.key <= "9") || // permite números de 0 a 9
          e.key === "Backspace" || // permite a tecla Backspace para apagar
          e.key === "Delete" || // permite a tecla Delete para apagar
          e.key === "Tab" || // permite a tecla Tab para navegar
          e.key === "ArrowLeft" || // permite a tecla de seta para a esquerda para navegar
          e.key === "ArrowRight" || // permite a tecla de seta para a direita para navegar
          e.key === "R" ||
          e.key === "," ||
          e.key === "." ||
          e.key === "$"
        )
      ) {
        e.preventDefault(); // impede a entrada do caractere não permitido
      }
    }}
    required
    placeholder="Preço:"
    data-tip= "Formato R$00,00"
    />
  </div>

</div>

          <div className="block">
            <h2>Arquivos do Paciente</h2>
            <p>

            </p>
          </div>
          <div className="block">
            <h2>Deseja confirmar a consulta?</h2>
            <button className="circle4-button">&gt;</button>
          </div>
        </div>
      </div>
    </article>
  );
};

export default Widget;
