import React, { useState, useEffect } from "react";
import moment from "moment";
import "./Calendar.css";
import Widget from "../widgets/widget-2/Widget";
import "moment/locale/pt-br";
import img1 from "../../Imagens/prox.png";
import img2 from "../../Imagens/ant.png";
moment.locale("pt-br");

function getDaysInMonth(month, year) {
  // ...
}

const Calendar = ({ colorCode }) => {
  const [currentMonth, setCurrentMonth] = useState(moment());
  const weeks = getDaysInMonth(currentMonth.month() + 1, currentMonth.year());
  const colorCode2 = 2;
  const [showWidget, setShowWidget] = useState(false);
  const [isMobile, setIsMobile] = useState(false);

  // Função para verificar se o dispositivo é móvel
  const checkIsMobile = () => {
    const isMobile = window.innerWidth <= 768; // Define o limite de largura para dispositivos móveis
    setIsMobile(isMobile);
  };

  useEffect(() => {
    // Verifica o tamanho da tela no carregamento inicial
    checkIsMobile();
    // Adiciona um ouvinte de redimensionamento da janela para atualizar a variável isMobile
    window.addEventListener("resize", checkIsMobile);
    // Remove o ouvinte de redimensionamento da janela ao desmontar o componente
    return () => {
      window.removeEventListener("resize", checkIsMobile);
    };
  }, []);

  function previousMonth() {
    // ...
  }

  function nextMonth() {
    // ...
  }

  function toggleWidget() {
    // ...
  }

  const button1Classes = `button1 ${
    colorCode === 0 ? "green" : colorCode === 1 ? "gray" : "pink"
  }`;
  const button2Classes = `button1 ${
    colorCode2 === 0 ? "green" : colorCode2 === 1 ? "gray" : "pink"
  }`;

  // Verifica se o dispositivo é móvel e renderiza a semana em vez do mês
  if (isMobile) {
    return (
      <>
        <div className="calendar-header">
          {showWidget && <Widget />}
          <button className="circle-button" onClick={toggleWidget}>
            Nova Consulta
          </button>

          <button className="circle2-button">+</button>
          <button className="botMes" onClick={previousMonth}>
            <img src={img2} alt="proximo" />
          </button>
          <h1 className="titMes">{currentMonth.format("MMMM YYYY").toUpperCase()}</h1>
          <button className="botMes" onClick={nextMonth}>
            <img src={img1} alt="proximo" />
          </button>
        </div>

        <table className="calendar-table">
          <tbody>
            {weeks.map((week, index) => (
              <tr key={index}>
                {week.map((day) => {
                  const isCurrentDay = day.isSame(moment(), "day");
                  const isNextMonth = day.month() !== currentMonth.month();
                  const dayClasses = isCurrentDay
                    ? "BlocoDia current-day"
                    : isNextMonth
                    ? "BlocoDia cinza"
                    : "BlocoDia";

                  return (
                    <td className={dayClasses} key={day.format("D MMMM YYYY")}>
                      <div>
                        <div className="DataDia">{day.format("ddd").toUpperCase()}</div>
                        <div className="DataDia2">{day.format("D")}</div>
                      </div>
                      <div>
                        <button className={button1Classes}>
                          <h1 className="Titulo2bnt">Fernanda Andrade da Silva</h1>
                        </button>
                        <button className={button2Classes}>
                          <h1 className="Titulo2bnt">Guilherme Fernandes</h1>
                        </button>
                        <button className="buttonVerTodos">Mostrar Todos</button>
                      </div>
                    </td>
                  );
                })}
              </tr>
            ))}
          </tbody>
        </table>
      </>
    );
  }

  // Renderiza o calendário do mês completo para dispositivos não móveis
  return (
    <>
      {/* ... */}
    </>
  );
};

export default Calendar;
