import React, { useState, useEffect } from "react";
import "./Widget.css";
import close1 from "../../../Imagens/closeR.png";

const menuHeight = getComputedStyle(document.documentElement).getPropertyValue("--menu-height");

const buttons = ["Dados do Paciente", "Dados da Consulta", "Arquivos", "Finalizar"];

function Widget() {
  const [activeBlock, setActiveBlock] = useState(0);
  const [isWidgetOpen, setIsWidgetOpen] = useState(true);

  const toggleMenuBlock = (index) => {
    setActiveBlock(index);
  };

  const closeWidget = () => {
    setIsWidgetOpen(false); // Define isWidgetOpen como false para fechar o widget
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (!event.target.closest(".card")) {
        setIsWidgetOpen(false);
      }
    };

    document.addEventListener("click", handleClickOutside);

    return () => {
      document.removeEventListener("click", handleClickOutside);
    };
  }, []);

  if (!isWidgetOpen) {
    return null; // Retorna null para não renderizar o widget quando estiver fechado
  }

  return (
    <article className={`card ${activeBlock === 0 ? "open" : ""}`}>
      <div className="buttons">
        {buttons.map((button, index) => (
          <button
            className={index === activeBlock ? "active" : ""}
            onClick={() => toggleMenuBlock(index)}
            key={index} // Add key prop here with a unique value
          >
            {button}
          </button>
        ))}
      </div>

      <div className="wrapper">
        <button className="close-button" onClick={closeWidget}>
          <img src={close1} alt="fechar" />
        </button> {/* Botão para fechar o widget */}
        <div
          className="content"
          style={{ transform: `translate(0, calc(0px - ${menuHeight} * ${activeBlock}))` }}
        >
          {/* Conteúdo dos blocos do menu */}
        </div>
      </div>
    </article>
  );
}

export default Widget;
