import React, { useState, useEffect, useContext } from "react";
import axios from "axios";
import "./Widget.css";
import { UserContext } from "../../../UserContext";

const menuHeight = getComputedStyle(document.documentElement).getPropertyValue("--menu-height");

const buttons = ["Dados do Paciente", "Dados da Consulta", "Arquivos", "Finalizar"];

function Widget() {
  const [activeBlock, setActiveBlock] = useState(0);
  const [isWidgetOpen, setIsWidgetOpen] = useState(true);
  const { crp } = useContext(UserContext);
  const [formValues, setFormValues] = useState({
    nome: "",
    cpf: "",
    email: "",
    local: "",
    recorrencia: "",
    dataInicio: "",
    dataFim: "",
    horario: "",
    preco: "",
  });
  const [nomes, setNomes] = useState([]);

  const toggleMenuBlock = (index) => {
    if (index === activeBlock || canSwitchBlock()) {
      setActiveBlock(index);
    }
  };

  const closeWidget = (event) => {
    event.stopPropagation();
    setIsWidgetOpen(false);
  };

  const canSwitchBlock = () => {
    switch (activeBlock) {
      case 0:
        return formValues.nome !== "" && formValues.cpf !== "" && formValues.email !== "";
      case 1:
        return (
          formValues.local !== "" &&
          formValues.recorrencia !== "" &&
          formValues.dataInicio !== "" &&
          formValues.dataFim !== "" &&
          formValues.horario !== "" &&
          formValues.preco !== ""
        );
      case 2:
        return true;
      default:
        return false;
    }
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormValues((prevValues) => ({
      ...prevValues,
      [name]: value,
    }));

    if (name === 'nome') {
      const selectedPatient = nomes.find((item) => item.nome === value);
      if (selectedPatient) {
        setFormValues((prevValues) => ({
          ...prevValues,
          cpf: selectedPatient.cpf,
          email: selectedPatient.email,
        }));
      }
    }
  };


  const handleRecorrenciaChange = (event) => {
    const { value } = event.target;
    setFormValues((prevValues) => ({
      ...prevValues,
      recorrencia: value,
    }));
  };

  const handleNomeChange = (event) => {
    const { value } = event.target;
    setFormValues((prevValues) => ({
      ...prevValues,
      nome: value,
    }));
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://192.168.0.9:8080/professional/patients/${crp}`);
        const data = response.data;
        const names = data.object.map((item) => item.name);
        if (Array.isArray(names)) {
          setNomes(names);
        } else {
          console.error("Os dados obtidos nÃ£o sÃ£o um array:", names);
        }
      } catch (error) {
        console.error("Erro ao obter os nomes da base de dados:", error);
      }
    };

    fetchData(); // Chama a funÃ§Ã£o fetchData para buscar os nomes
  }, [crp]);

  if (!isWidgetOpen) {
    return null;
  }

  return (
    <article className={`card ${activeBlock === 0 ? "open" : ""}`}>
      <div className="buttons">
        {buttons.map((button, index) => (
          <button
            className={index === activeBlock ? "active" : ""}
            onClick={() => toggleMenuBlock(index)}
            key={index}
          >
            {button}
          </button>
        ))}
        <button className="close-button" onClick={closeWidget}>
          FECHAR
        </button>
      </div>

      <div className="wrapper">
        <div className="content" style={{ transform: `translate(0, calc(0px - ${menuHeight} * ${activeBlock}))` }}>
          <div className="block">
            <h2>Dados do Paciente</h2>
            <select
