import { useState } from "react";
import { useNavigate } from "react-router-dom";
import exit from "./../../Imagens/exit.png";
import axios from 'axios';

function CadPaciente() {
  const navigate = useNavigate();
  const [isLoading, setIsLoading] = useState(false);
  const [isConfigOpen, setIsConfigOpen] = useState(true);
  const [crp, setCrp] = useState("");
  const [name, setName] = useState("");
  const [phoneNumber, setPhoneNumber] = useState("");
  const [dateOfBirth, setDateOfBirth] = useState("");
  const [emailAddress, setEmailAddress] = useState("");
  const [city, setCity] = useState("");
  const [state, setState] = useState("");
  const [country, setCountry] = useState("");
  const [street, setStreet] = useState("");
  const [postalCode, setPostalCode] = useState("");

  function encerrarSessao() {
    setIsLoading(true);

    // Simulando o carregamento com um setTimeout
    setTimeout(() => {
      setIsLoading(false);
      navigate("/");
    }, 2000);
  }

  function alterarSenha() {
    // Lógica para alterar a senha aqui
  }
  function closeConfig() {
    setIsConfigOpen(false);
  }
  if (isConfigOpen===false) {
    return null;
  }
  async function fetchAddress() {
    const cep = postalCode.replace(/\D/g, ''); // Remove caracteres não numéricos do CEP
  
    try {
      const response = await axios.get(`https://viacep.com.br/ws/${cep}/json/`);
      const addressData = response.data;
  
      setState(addressData.uf); // Atualiza o campo "Estado" com a UF retornada pela API
      setCountry('Brasil'); // Define o país como "Brasil"
      setCity(addressData.localidade); // Atualiza o campo "Cidade" com a cidade retornada pela API
  
    } catch (error) {
      console.error('Error:', error);
      setCity("");
      setState("");
      setCountry("");
    }
  }






  return (
    <div style={{width:"10%", backgroundColor:"White", border:"0.1px solid grey", borderRadius:"10px", padding:"0.3%"}}>
      <div className="titleContainer">
      <h2 className="titulo">Cadastrar novo paciente</h2>
      <button className="bntfechar"  onClick={closeConfig}><img src={exit} alt="Imagem de Login" /></button>
      </div>
      
      <div className="criar-conta-container">
      <h1 className="titC">Criar Nova Conta</h1>
      <div className="campo-form">
        <input className="input-c"
          type="text"
          value={crp}
          placeholder="CRP:"
          onChange={(e) => setCrp(e.target.value)}
        />
      </div>
      <div className="campo-form">
        <input className="input-c"
          type="text"
          value={name}
          placeholder="Nome:"
          onChange={(e) => setName(e.target.value)}
        />
      </div>
      <div className="campo-form">
        <input className="input-c"
          type="text"
          value={phoneNumber}
          placeholder="Telefone:"
          onChange={(e) => setPhoneNumber(e.target.value)}
        />
      </div>
      <div className="campo-form">
        <input className="input-c"
          type="text"
          value={dateOfBirth}
          placeholder="Data de Nascimento (dd/mm/aaaa):"
          onChange={(e) => setDateOfBirth(e.target.value)}
        />
      </div>
      <div className="campo-form">
        <input className="input-c"
          type="email"
          value={emailAddress}
          placeholder="Endereço de Email:"
          onChange={(e) => setEmailAddress(e.target.value)}
        />
      </div>
      <div className="campo-form">
        <input className="input-c"
          type="text"
          value={postalCode}
          placeholder="CEP:"
          onChange={(e) => setPostalCode(e.target.value)}
          onBlur={fetchAddress}
      />

      </div>
 
      <div className="campo-form">
        <input className="input-c"
          type="text"
          value={street}
          placeholder="Rua, número e complemento (se houver):"
          onChange={(e) => setStreet(e.target.value)}
        />
      </div>

      <div className="campo-form">
        <input className="input-c"
          type="text"
          value={city}
          placeholder="Cidade:"
          onChange={(e) => setCity(e.target.value)}          
        />
      </div>
      <div className="campo-form">
        <input className="input-c"
          type="text"
          value={state}
          placeholder="Estado:"
          onChange={(e) => setState(e.target.value)}          
        />
      </div>
      <div className="campo-form">
        <input className="input-c"
          type="text"
          value={country}
          placeholder="País:"
          onChange={(e) => setCountry(e.target.value)}          
        />
      </div>
      
      
      <div className="campo-form">
        <input className="input-c"
          type="password"
          value={password}
          placeholder="Senha:"
          onChange={(e) => setPassword(e.target.value)}
        />
      </div>
      <button className="criar-conta-button" onClick={handleCriarConta}>
        Criar Conta
      </button>
      {isLoading && (
        <>
          <div className="loading-bar2">
            <div className="loading-bar2-fill" />
          </div>
          <p style={{ fontSize: "12px" }}>Fazendo Logoff...</p>
        </>
      )}
</div>
    </div>
  );
}

export default CadPaciente;
