import React, { useState, useEffect, useContext } from 'react';
import { UserContext } from '../../UserContext';
import "./Inicio.css";
import Menu from "../../Menu";
import Header from '../../Header';
import Calendar from './Calendar';
import axios from 'axios';
import "../../Fonts.css";

const Inicio = () => {
  const colorCode = 0;
  const [appointments, setAppointments] = useState([]); 
  const [profissional, setProfissional] = useState([]);  
  const { setNome } = useContext(UserContext);
  const { email } = useContext(UserContext);
  useEffect(() => {
    axios.get(`http://192.168.0.9:8080/professional/${email}`)
      .then(response => {        
        setProfissional(response.data.object);
        if (profissional && profissional.name) {
          setNome(profissional.name);
        }           
      })
      .catch(error => {
        console.error(error);
      });
  }, [email, setNome, profissional]);

  useEffect(() => {

      axios.get(`http://192.168.0.9:8080/appointment/${profissional.crp}`)
        .then(response => {
          setAppointments(response.data.object);
          console.log(appointments.appointments.date);
        })
        .catch(error => {
          console.error(error);
        });

  }, [profissional.crp]);


  if (!profissional) {
    // Data is still loading, show a loading spinner or message
    return <div>Loading...</div>;    
  }

  return (
    <div className="container">
      <div className="item item1-1" style={{ width: '93%' }}><Menu /></div>
      <div className="item item1-2"><Header/></div>
      <div className="item item2-1"><Calendar colorCode={colorCode} appointments={appointments} /></div>
    </div>
  );
};

export default Inicio;
